{"filter":false,"title":"auto_process_insulin.py","tooltip":"/raw_data/auto_process_insulin.py","undoManager":{"mark":0,"position":0,"stack":[[{"start":{"row":0,"column":0},"end":{"row":52,"column":0},"action":"insert","lines":["import re","import os","","# Folder input & output","RAW_DIR = \"raw_data\"","OUT_DIR = \"processed\"","","# Pastikan folder output ada","os.makedirs(OUT_DIR, exist_ok=True)","","def process_file(filepath):","    \"\"\"Bersihkan dan potong file insulin\"\"\"","    with open(filepath) as f:","        raw_data = f.read()","","    # Bersihkan data","    cleaned = raw_data.replace(\"ORIGIN\", \"\").replace(\"//\", \"\")","    cleaned = re.sub(r'[^a-zA-Z]', '', cleaned).lower()","","    # Nama file dasar","    filename = os.path.splitext(os.path.basename(filepath))[0]","","    # Simpan full sequence","    clean_file = os.path.join(OUT_DIR, f\"{filename}-seq-clean.txt\")","    with open(clean_file, \"w\") as f:","        f.write(cleaned)","","    print(f\"{filename}: Full sequence length = {len(cleaned)}\")","","    # Potong bagian","    lsinsulin = cleaned[0:24]","    binsulin = cleaned[24:54]","    cinsulin = cleaned[54:89]","    ainsulin = cleaned[89:110]","","    # Simpan bagian","    with open(os.path.join(OUT_DIR, f\"ls{filename}-seq-clean.txt\"), \"w\") as f: f.write(lsinsulin)","    with open(os.path.join(OUT_DIR, f\"b{filename}-seq-clean.txt\"), \"w\") as f: f.write(binsulin)","    with open(os.path.join(OUT_DIR, f\"c{filename}-seq-clean.txt\"), \"w\") as f: f.write(cinsulin)","    with open(os.path.join(OUT_DIR, f\"a{filename}-seq-clean.txt\"), \"w\") as f: f.write(ainsulin)","","    print(f\"  Signal (ls): {len(lsinsulin)} | B-chain: {len(binsulin)} | \"","          f\"C-peptide: {len(cinsulin)} | A-chain: {len(ainsulin)}\")","","def main():","    # Scan semua file di folder raw_data","    for file in os.listdir(RAW_DIR):","        if file.endswith(\".txt\"):","            process_file(os.path.join(RAW_DIR, file))","","if __name__ == \"__main__\":","    main()",""],"id":1}]]},"ace":{"folds":[],"scrolltop":422,"scrollleft":0,"selection":{"start":{"row":52,"column":0},"end":{"row":52,"column":0},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":29,"state":"start","mode":"ace/mode/python"}},"timestamp":1755749200768,"hash":"4583f5c63d89338138e4d03501d0c9ab2c6ed41d"}